<?phpdefine('_CFEXEC', true);require('engine/modules/server/support/config.php');//error_reporting(0);if (!(empty($_POST['nickname']))) {    $nickname = mysql_real_escape_string(trim((string)$_POST['nickname']));    $config['mysql_table'] = "banlist";    $table = $config['mysql_table'];    $sql = mysql_query("SELECT * FROM $table WHERE `name` = '$nickname' ORDER BY `id` DESC");} else {    $config['mysql_table'] = "banlist";    $table = $config['mysql_table'];    $sql = mysql_query("SELECT * FROM $table ORDER BY `id` DESC LIMIT 20");}/*if(!(empty($nickname))) {        $result = mysql_query('SELECT * FROM '.$mysql_table.' WHERE name = "'.$nickname.'" ');    } else {           $result = mysql_query('SELECT * FROM '.$mysql_table.' ORDER BY id DESC');}*/?><i>20 последних</i><form id="banview" name="id" method="POST" action="">    <input type="text" name="nickname" id="nickname" maxlength="32" size="15" placeholder="Никнейм"           pattern="^[a-zA-Z0-9_]+$" required>    <input type="submit" name="view" id="view" value="Поиск" class="btn btn-small btn-success"></form><table width=100% cellspacing="0" class="table table-bordered">    <tr valign="top" id="top">        <th bgcolor="#D5F2FF">            <center>ID</center>        </th>        <th bgcolor="#D5F2FF">            <center>Никнейм</center>        </th>        <th bgcolor="#D5F2FF">            <center>Действие</center>        </th>        <th bgcolor="#D5F2FF">            <center>Причина</center>        </th>        <th bgcolor="#D5F2FF">            <center>Кем выдано</center>        </th>        <th bgcolor="#D5F2FF">            <center>Время выдачи</center>        </th>        <th bgcolor="#D5F2FF">            <center>Время разбана</center>        </th>    </tr>    <?php    while ($data = mysql_fetch_assoc($sql)) {        /*if($col == "#eeeeee"){        $col = "#ffffff";        }else{        $col = "#ffffff";        }*/        echo "<tr>";        echo "<td><center>" . $data['id'] . "</center></td>";        echo "<td><center>" . $data['name'] . "</center></td>";        if ($data['type'] == "5") {            echo "<td><center>Разбан</center></td>";        } else {            if ($data['type'] == "3") {                echo "<td><center>Кик</center></td>";            } else {                if ($data['type'] == "2") {                    echo "<td><center>Предупреждение</center></td>";                } else {                    if ($data['type'] == "0") {                        echo "<td><center>Забанен</center></td>";                    } else {                        if ($data['type'] == "1") {                            echo "<td><center>Забанен IP</center></td>";                        } else {                            if ($data['type'] == "4") {                                echo "<td><center>Fine</center></td>";                            } else {                                if ($data['type'] == "6") {                                    echo "<td><center>Jailed</center></td>";                                } else {                                    if ($data['type'] == "9") {                                        echo "<td><center>Навсегда</center></td>";                                    } else {                                        if ($data['type'] == "7") {                                            echo "<td><center>Мьют</center></td>";                                        } else {                                            echo "<td><center>Unknown</center></td>";                                        }                                    }                                }                            }                        }                    }                }            }        }        echo "<td><center>" . $data['reason'] . "</center></td>";        echo "<td><center>" . $data['admin'] . "</center></td>";//$bantime = date("d.m.Y H:i", $data['time']);        $bantime = cf_api::dateconvert($data['time']);        echo "<td><center>$bantime</center></td>";        if ($data['temptime'] == "0") {            if (!$data['type'] == '2') {                echo "<td><center><span style='color: red;'>&infin;</span></center></td>";            } else {                echo "<td><center>—</center></td>";            }        } else {            $unbantime = cf_api::dateconvert($data['temptime']);            echo "<td><center>$unbantime</center></td>";        }        echo "</tr>";    }    ?></table>